Loop 
      INCF  PORTC, 1    ;Incrementar el registre associat a PORTC
      MOVF  PORTA, 0, 1 ;W=PORTA
      ;XORLW 1 ;ACTIVAR AQUESTA LINEA PER INVERTIR EL FUNCIONAMENT
      MOVWF PORTB, 1    ;PORTB=W
      NOP               ;No Operation
      NOP               ;No Operation
      goto  Loop

